import React from "react";
import { useRef } from "react";
import { useState } from "react";
import {useResolvedPath} from "react-router-dom"
import Average from "./Average";
import Item from "./item";

function ItemList() {
    let numberRef = useRef(2);
    console.log("ItemList component render");
    const [text, setText] = useState("");
    const [users, setUsers] = useState([
        {
            id:0,
            name:"test1",
            age:30,
            score:100
        },
        {
            id:1,
            name:"test2",
            age:27,
            score:75
        },
    ]);

    const addUser = () => {
        setUsers([
            ...users,
            {
                id: (numberRef.current += 1),
                name: "test",
                age: 30,
                score: 90
            }
        ]);
    };

    const getResult = (score) =>{
        if(score <= 70){
            return {grade: "D"};
        }else if(score <= 80){
            return {grade: "C"};
        }
        else if(score <= 90){
            return {grade: "B"};
        }else{
            return {grade: "A"};
        }
    }

    const average = (() => {
        console.log("average");
        // 누적값
        return users.reduce((acc, cur) => {
            return acc+cur.age / users.length;
        }, 0)
    })();
    // 이렇게 함수 뒤에 () 붙히면 바로 즉시 실행하겠다는 뜻이다!

    return(
        <div>
            <div>
                <input
                    type="text"
                    value={text}
                    placeholder="아무내용 입력"
                    onChange={(e)=>{
                            setText(e.target.value);
                            // 최적화 
                        }
                    } />
            </div>
            
            <Average average={average}></Average>

            <button onClick={addUser}>새 유저 생성</button>
            {users.map((user, idx) => {
                return (
                    <Item></Item>
                    /*<div key={idx}>
                        <div>이름 : {user.name}</div>
                        <div>나이 : {user.age}</div>
                        <div>점수 : {user.score}</div>
                        <div>등급 : {getResult(user.score).grade}</div>
                    </div>*/
                )
            })}
        </div>
    )
}

export default ItemList;
