import React, { useEffect } from "react";
import { useRef } from "react";
import { useCallback } from "react";
import { useState } from "react";
import registerAxios from "../lib/Register_Axios";
import Logo from "../asset/img/logo.png";


function Register({
    btnClick,
    btnName,
    isClickFilterBtn,
    children,
  }){
    let ref = useRef(null);
    const myIdRef = useRef(null);
    const [user_id, setId]=useState("");
    const [user_pwd, setPwd]=useState("");
    const [user_name, setName]=useState("");
    const [user_tel1, setTel1]=useState("");
    const [user_tel2, setTel2]=useState("");
    const [user_email, setEmail]=useState("");
    const [user_postcode, setPostcode]=useState("");
    const [user_addr, setAddr]=useState("");
    const [user_detailAddr, setDetailAddr]=useState("");
    const [member, setMember] = useState([
        {
            id : '',
            pw : '',
            
        }
    ]);
    
    useEffect(() => {
        document.addEventListener('mousedown', clickModalOutside);
        return () => {
          document.removeEventListener('mousedown', clickModalOutside);
        };
      });
    
      const clickModalOutside = event => {
        if (btnClick && !myIdRef.current.contains(event.target)) {
          isClickFilterBtn();
        }
      };

    const dupCheck = useEffect (() => {
        registerAxios.post("/dupCheck",{
          id : user_id,
        },{"Content-Type": 'application/json'})
        .then(response => {
          console.log(response);
          let res = response.data;
          console.log(res);
          if (res === "0"){
            alert("사용가능한 ID!");
          }else{
            alert("이미 존재하는 ID!");
            // 아이디에 focus 줌
          }
        }).catch(err =>{ console.log("error!!!! =>",err) })
      });
  
  
      const validateForm = () => {
        if ((user_id).length > 0 && (user_pwd).length > 0 && (user_email).length > 0){
          return false;
        }else{
          return true;
        }
      } 

      const handleSubmit = (event) => {
          event.preventDefault();
      }

    return(
        <div className="card m-auto">
            <div className="card-body m-auto">
            <picture>
                <source srcSet={Logo} media="all and (min-width: 800px)"/> 
                <img src={Logo} alt="이포넷 로고 이미지" />
            </picture>
                <form className="register-form form">
                    <div className="row-group">
                        <div className="join-row">
                            <h3 className="join-title">
                                <label htmlFor="id">ID</label>
                            </h3>
                            <span ref={myIdRef}>
                            <input type="text" className="form-control mr-3 my-0 lt-6" style={{display:'inline', width:"70%"}} 
                            name="id" id="id"
                            ref={ref}
                            onKeyUp={() => {
                                let inputId = ref.current.value.toLowerCase();
                                setTimeout(() => {
                                    if(inputId === ref.current.value.toLowerCase()){
                                        setId(ref);
                                    }
                                },400);
                            }}
                            onMouseOutCapture
                            required/>
                            </span>
                            <button size="lg" className="btn btn-outline-danger"  style={{display:'inline'}}
                            disabled={!(user_id.length > 0)} onClick={
                                () => {dupCheck()}
                            }>중복체크</button>

                            <span className="error_next_box" id="idMsg" style={{ariaLive:"assertive"}}>
                                필수 정보입니다.
                            </span>
                        </div>
                    </div>
                    <div className="form-group">
                        <label className="">PWD</label>
                        <input type="password" className="form-control mx-auto my-0" style={{width:'90%'}} 
                        placeholder="Password"
                        required/>
                    </div> 
                </form>
            </div>
        </div>
    )
}

export default Register;
