                id : (numberRef.current += 1),
                name : "test",
                age :30,
                score :90
            }
        ]);
    }
    const getResult = (score) =>{
        if(score < 70){
            return {grade: "D"};
        }else if(score <= 80){
            return {grade: "C"};
        }
        else if(score <= 90){
            return {grade: "B"};
        }else{
            return {grade: "A"};
        }
    }
    function average(){
        console.log("average");
        return users.reduce((acc, cur) => {
            return acc+cur.age / users.length;
        }, 0)
    }
    return(
        <div>
            <div>
                <input
                    type="text"
                    value={text}
                    placeholder="아무내용 입력"
                    onChange={(e)=>{
                            setText(e.target.value);
                            // 최적화 
                        }
                    } />
            </div>
            <div>평균 나이 : {average}</div>
            <button onClick={addUser}>새 유저 생성</button>
           
        </div>
    )
}

export default ItemList;
