import React from "react";

function StateTest(){
    var count = 0;
    return(
        <div>
            <p> {count} </p>
            <button onClick={() => {
                count++;
                console.log(count);
            }}>스테이트 안쓰면 렌더링이 안되지롱 눌러봐눌러봐눌러봐</button>
        </div>
    )
}

export default StateTest;
