import React, { useRef } from "react";
import { useState } from "react";

//npm install react-bootstrap bootstrap
// index.js에다가  import "bootstrap/dist/css/bootstrap.min.css" 하셈
// http://bootstrapk.com/css/#tables

function Login(){
    let fomrRef = useRef(null);
    const [id, setID] = useState("");
    const [pwd, setPwd] = useState("");
    return(
        <div className="login">
            <div className='card1 mb-4'>
                <div className="card-body" style={{border:'1px black solid', borderRadius:'.5rem'}}>
                    <h2 style={{color:'#f13645'}} className="mx-auto">E4. Pay Service</h2>
                    <p style={{lineHeight:'140px', height:'182.5px', margin:"0px"}}>서비스 이용을 위해서는 로그인이 필요합니다.</p>
                </div>
            </div>
            <div className='card mx-auto mt-3'>
                <div className="card-body p-1">
                    <form className="form">
                        <div className="form-group">
                            <input type="text" className="form-control mx-auto my-2" style={{width:'90%', textAlign:'center'}} 
                            placeholder="ID"
                            value={id}
                            onKeyUp={() => {
                                let search = searRef.current.value.toLowerCase();
                                setTimeout(() => {
                                    if(search === searRef.current.value.toLowerCase()){
                                        setID(searRef);
                                    }
                                },400);
                            }}
                            required/>
                        </div>
                        <div className="form-group">
                            <input type="password" className="form-control mx-auto my-2" style={{width:'90%', textAlign:'center'}} placeholder="Password" required/>
                        </div>
                        <div className="button-container my-auto">
                            <button type="submit" className="loginBtn btn btn-danger btn-block mx-auto my-2" style={{width:'90%'}}>로그인</button>
                        </div>
                    </form>
                    <ul className="find_wrap" style={{listStyle:'none'}}>
                        <li><a className="find_text" target={'_blank'}>비밀번호 찾기</a></li>
                        <li><a  className="find_text" target={'_blank'}>아이디 찾기</a></li>
                        <li><a  className="find_text" target={'_blank'}>회원가입</a></li>
                    </ul>
                </div>
            </div>
        </div>
    )
}

export default Login;
