import { Outlet } from "react-router-dom"
import React, {useState, memo, useRef} from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./Header";
import SideBar from "./NavBar";
import "../css/main.css";
import ChargeCash from "./ChargeCash";
import { Form } from "react-bootstrap";
import Center from "./Center";
import { useEffect } from "react";


const MainLayOut = () => {
    console.log("메인레이아웃 렌더링")
    // const [title, setTitle] = useState("머니 충전");
    const header = useRef(<Header />);

    const setTitle =(newTitle) => {
       
       console.log(document.getElementById("mainTitle").textContent);
       document.getElementById("mainTitle").textContent = newTitle;
    }
    return(
    <div>
        <Header />
        <Center>
            <SideBar setTitle={setTitle}/>
            <section style={{aligIitems: "center"}} className="m-auto section">
                <article className="m-auto">
                    <Form>
                        <Outlet />
                    </Form>
                </article>
            </section>
        </Center>
    </div>);
}

export default memo(MainLayOut);
