import React from "react";
import { useRef } from "react";
import { useCallback } from "react";
import { useState } from "react";
import registerAxios from "../lib/Register_Axios";

function Register(){
    let ref = useRef(null);
    const [user_id, setId]=useState("");
    const [user_pwd, setPwd]=useState("");
    const [user_name, setName]=useState("");
    const [user_tel1, setTel1]=useState("");
    const [user_tel2, setTel2]=useState("");
    const [user_email, setEmail]=useState("");
    const [user_postcode, setPostcode]=useState("");
    const [user_addr, setAddr]=useState("");
    const [user_detailAddr, setDetailAddr]=useState("");

    const dupCheck = useCallback (() => {
        registerAxios.post("/dupCheck",{
          id : this.state.id,
        },{"Content-Type": 'application/json'})
        .then(response => {
          console.log(response);
          let res = response.data;
          console.log(res);
          if (res === "0"){
            alert("사용가능한 ID!");
          }else{
            alert("이미 존재하는 ID!");
            // 아이디에 focus 줌
          }
        }).catch(err =>{ console.log("error!!!! =>",err) })
      })
  
  
      const validateForm = () => {
        if ((this.state.id).length > 0 && (this.state.pwd).length > 0 && (this.state.email).length > 0){
          return false;
        }else{
          return true;
        }
      } 

      const handleSubmit = (event) => {
          event.preventDefault();
      }

    return(
        <div className="card m-auto">
            <div className="card-body m-auto">
                <form className="register-form form">
                    <div className="form-group">
                        <label className="">ID</label>
                        <input type="text" block="false" className="form-control mx-auto my-2" style={{width:'90%'}} 
                        placeholder="ID"
                        ref={idRef}
                            onKeyUp={(e) => {
                                let inputId = idRef.current.value.toLowerCase();
                                setTimeout(() => {
                                    if(inputId === idRef.current.value.toLowerCase()){
                                        setID(idRef);
                                    }
                                },400);
                            }}
                        required/>
                        <button block="false" size="lg" className="btn btn-sm btn-outline-danger" 
                        disabled={!(user_id.length > 0)} onClick={
                            () => {dupCheck()}
                        }>중복체크</button>
                    </div>
                    <div className="form-group">
                        <label className="">PWD</label>
                        <input type="password" className="form-control mx-auto my-2" style={{width:'90%'}} 
                        placeholder="Password"
                        required/>
                    </div> 
                </form>
            </div>
        </div>
    )
}

export default Register;
