import React from "react";
import { Nav } from "react-bootstrap";
import { NavLink } from "react-router-dom";

function SideBar({setTitle}){

    const menus = [
        {name : "머니 충전", path: "/main/chargeCash"},
        {name : "머니 결제", path: "/main/payCash"},
        {name : "거래내역", path: "/main/payHistory"},
    ]
    return(
    <nav style={{backgroundColor:"white", width:"15%", borderRight:""}}>
      <Nav defaultActiveKey={["/main/"]} className="flex-column" variant="pills" 
        style={{alignItems: "center", marginTop:"25px", fontSize:"1.2rem"}}>
          {menus.map((menu, idx) => {
            return(
              <Nav.Item style={{width: "90%", textAlign:"center"}} className="my-1">
                <NavLink key={idx} afterstyle={{color:"black"}} href={menu.path}
                onClick={() => {
                    setTitle(menu.name);
                }}
                >{menu.name}</NavLink>
              </Nav.Item>
            );
          })}
          
      </Nav>
    </nav>
    )
}

export default SideBar;
