import React, { useState } from "react";
import { Form, Table } from "react-bootstrap";

function PayCash(){
    const products = [{
        강릉정육점: {
            소고기 : 10000,
            돼지고기 : 3000,
            닭고기 : 8000,
            오리고기 : 12000
        },
        소파가구점 : {
            소파 : 120000,
            원목의자 : 10000,
            식탁 : 45000,
            선반 : 2000
        },
        알파문구 : {
            연필 :300,
            펜 : 1000,
            지우개 :5000,
            사인펜:15000
        }
    }]
    const [branch, setBranch] = useState({});
    const [product, setProduct] = useState({});
    return(
        <Table striped className="charge-cash-table">
        <tbody>
            <tr style={{borderTop:"0.7px white solid"}}>
                <td>가맹점</td>
                <td>
                    <Form.Select>
                        {products.array((dict) => {
                            return(
                                <option value={dict}>{dict}</option>
                            );
                        })}
                    </Form.Select>
                </td>
                <td></td>
            </tr>
            <tr>
                <td>구매물품</td>
                <td style={{width: "230px"}}>
                <input className="form-select" name="chargingCashs" 
                            list="chargingCash" id="chargingCashs" style={{width:"60%", display:"inline", marginRight:"7px"}}/>
                            <span> 원</span>
                            <datalist id="chargingCash" >
                                <option value="5000"/>
                                <option value="10000"/>
                                <option value="20000"/>
                                <option value="50000"/>
                                <option value="100000"/>
                            </datalist>
                
                </td>
                <td></td>
            </tr>
            <tr>
                <td>결제금액</td>
                <td></td>
                <td></td>
            </tr>
            <tr>
                <td>결제수단</td>
                    <td></td>
                    <td></td>
                </tr>
            <tr>
                <td colSpan="3" style={{textAlign:"center"}}>
                    <button type="button" className="btn btn-danger">결제하기</button>
                </td>
            </tr>
        </tbody>
    </Table> 
    );
}

export default PayCash;
